<?php

// Advent of code. Day Two, Part One.

class daytwo {
    private $instructions, $keypad, $x, $y, $code;

    public function __construct() {
        $this->instructions = explode(PHP_EOL, '



');

        $this->keypad = [
            [1, 2, 3],
            [4, 5, 6],
            [7, 8, 9],
        ];
        $this->x = 1;
        $this->y = 1;
    }

    private function move(string $dir) {
        if ($dir == 'U') {
            $this->x--;
            if ($this->x < 0) {
                $this->x = 0;
            }
        }
        if ($dir == 'D') {
            $this->x++;
            if ($this->x > 2) {
                $this->x = 2;
            }
        }
        if ($dir == 'L') {
            $this->y--;
            if ($this->y < 0) {
                $this->y = 0;
            }
        }
        if ($dir == 'R') {
            $this->y++;
            if ($this->y > 2) {
                $this->y = 2;
            }
        }
    }

    public function go() {
        foreach ($this->instructions as $instruction) {
            for ($j = 0; $j <= (strlen($instruction) - 1); $j++) {
                $this->move(substr($instruction, $j, 1));
            }
            $this->code .= $this->keypad[$this->x][$this->y];
        }
        echo 'The door code is ' . $this->code . '.' . PHP_EOL;
    }

}

$daytwo = new daytwo();
$daytwo->go();

// The door code is 18843.
